<template>
	<view class="container">
		<view class="address-list">
			<view class="a-address" v-for="(item,index) in dataList" :key="index">
				<view class="left-text">
					<view class="name-tel">
						{{item.title}}
					</view>
					<view class="address-box">
						{{item.province}}{{item.city}}{{item.adName}}
					</view>
				</view>
				<view class="right-edit" @tap="delAddressApi(item.userCommonAddressId)">
					<uni-icons type="trash-filled" size="30"></uni-icons>
				</view>
			</view>
		</view>
		<view class="bottom-box">
			<view class="add-btn" @tap="openMapSelect()">新增常用地址</view>
		</view>
		
	</view>
	<view v-if="dataList.length===0">
	<pinapp-empty-page></pinapp-empty-page>
	<uni-load-more status="noMore" />
	</view>
</template>

<script>
	import * as dd from "dingtalk-jsapi";
	import {
		getAddress,
		addAddress,
		delAddress
	} from '@/api/usualAddress.js'
	export default {
		data() {
			return {
				dataList: [],
				newAddress: {},
				addressList: [{
						id: 1,
						latitude: '',
						longitude: '',
						title: '春风动力',
						address: '浙江省杭州市临平区五洲路116号'
					},
					{
						id: 2,
						latitude: '',
						longitude: '',
						title: '春风动力',
						address: '浙江省杭州市临平区五洲路116号'
					},
					{
						id: 3,
						latitude: '',
						longitude: '',
						title: '春风动力',
						address: '浙江省杭州市临平区五洲路116号'
					}
				]
			}
		},
		onLoad() {
			this.getData()
		},
		methods: {
			getData() {
				getAddress().then(res => {
					this.dataList = res.data
				})
			},
			delAddressApi(id) {
				let obj = {}
				obj.userCommonAddressId = id
				delAddress(obj).then(res => {
					if (res.code === 200) {
						uni.showToast({
							icon:'none',
							title: "删除成功",
						})
						this.getData()
					} else {
						uni.showToast({
							icon:'none',
							title: res.msg
						})
					}
				})
			},
			addAddressApi() {
				addAddress(this.newAddress).then(res => {
					if (res.code === 200) {
						uni.showToast({
							icon:'none',
							title: "新增成功",
						})
						this.getData()
					} else {
						uni.showToast({
							icon:'none',
							title: res.msg
						})
					}
				})
			},
			inputDialogToggle() {
				this.$refs.inputDialog.open()
			},
			openMapSelect() {
				// 地图调用				
				let _this = this;
				// 地图定位
				dd.biz.map.locate({
					latitude: 30.4375621, // 纬度，非必须
					longitude: 120.2513994, // 经度，非必须
					onSuccess: function(obj) {		
						_this.newAddress.title = obj.title
						_this.newAddress.province = obj.province
						_this.newAddress.city = obj.city
						_this.newAddress.adName = obj.adName
						_this.newAddress.latitude = obj.latitude
						_this.newAddress.longitude = obj.longitude
						
						_this.addAddressApi()
					},
					onFail: function(err) {
						
					}
				});
			}
		}
	}
</script>

